package JavaTutorial;

public class DoMath2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		double score = 1.0 + 2.0 * 3.0; 
		System.out.println(score); 
		double copy = score;
		copy = copy / 2.0; 
		System.out.println(copy); 
		System.out.println(score);
	}

}
