package JavaTutorial;

public class forLoop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		/** Syntax for "for" loop is 
		 * for (initialization;condition;update) {
		 * 		statements
		 * }
		 */
		
		//example
		for (int i = 0; i < 3; i += 1) { 
			System.out.println("Rule #" + i);
		}
	}

}
